package alg2;public class ListenGlied {	protected Keyable o;	protected ListenGlied next;		public ListenGlied(Keyable o) {		this.o = o;		this.next = null;	}	public void setNext(ListenGlied o) {		next = o;	}		public ListenGlied getNext() {		return next;	}		public Keyable getContent() {		return o;	}		boolean hasNext() {		return next != null;	}		public boolean isEmpty() {		return o == null;	}}