package roughdraft;public class QuickSort {	public static int[] quickSort(int[] a) {		return quickSort(a, 0, a.length - 1);	}	public static int[] quickSort(int[] a, int li, int re) {		int rep = re;		int lip = li;		int piv = a[lip];		Visualize.printArray(a, lip, li, re, "");		while (li < re) {			if (a[li + 1] <= piv) {				int temp = a[li];				a[li] = a[li + 1];				a[li + 1] = temp;				li++;			} else if (a[li + 1] > piv) {				int temp = a[li + 1];				a[li + 1] = a[re];				a[re] = temp;				re--;			}			Visualize.printArray(a, lip, li, re, "");		}		if (li > lip + 1) {			quickSort(a, lip, li - 1);		}		if (li < rep - 1) {			quickSort(a, li + 1, rep);		}		return a;	}}