package roughdraft;public class Visualize {	public static void printArray(int[] a, String label) {		printArray(a, -1, -1, -1, label);	}	public static void printArray(int[] a) {		printArray(a, -1, -1, -1, "");	}	public static void printArray(int[] a, int cursor1, int cursor2) {		printArray(a, -1, cursor1, cursor2, "");	}	public static void printArray(int[] a, int cursor1) {		printArray(a, -1, -1, cursor1, "");	}	public static void printArray(int[] a, int cursor1, int cursor2,			int cursor3, String label) {		if (cursor1 != -1 && cursor2 != -1 && cursor3 != -1) {			for (int i = 0; i < a.length; i++) {				StringBuilder sb = new StringBuilder();				sb.append("<");				if (i == cursor1 && cursor1 != -1) {					sb.append("A");				}				if (i == cursor2 && cursor2 != -1) {					sb.append("B");				}				if (i == cursor3 && cursor3 != -1) {					sb.append("C");				}				sb.append(">");				System.out.printf("%5s%3s", sb, "|");				System.out.println();			}		}		System.out.printf("%20s", label);		for (int i = 0; i < a.length; i++) {			System.out.printf("%5d%3s", a[i], "|");		}		System.out.println();		System.out.println();	}}