package pr2.a05.erste;import java.awt.event.ActionListener;import lernhilfe2013ss.event.Timer;import pr2.a05.WetterPrinter;import pr2.a05.Wetterzustand2;public class WetterSimulation {	public static void main(String[] args) {		Timer hochSerimas = new Timer(200);		Timer tiefKatrina = new Timer(300);		Timer mittelhochSven = new Timer(400);		Timer tieftiefRolf = new Timer(600);		Timer extratiefCorinna = new Timer(800);				WetterPrinter wetterDisplay = new WetterPrinter();		ActionListener anzeige1 = new Wetteranzeige();		ActionListener anzeige2 = new Wetteranzeige();		hochSerimas.setActionCommand(Wetterzustand2.BEDECKT);		tiefKatrina.setActionCommand(Wetterzustand2.SONNIG);		mittelhochSven.setActionCommand(Wetterzustand2.WOLKIG);		tieftiefRolf.setActionCommand(Wetterzustand2.REGEN);		extratiefCorinna.setActionCommand(Wetterzustand2.GEWITTER);				hochSerimas.addActionListener(wetterDisplay);		tiefKatrina.addActionListener(wetterDisplay);		mittelhochSven.addActionListener(wetterDisplay);		tieftiefRolf.addActionListener(wetterDisplay);		extratiefCorinna.addActionListener(wetterDisplay);		hochSerimas.addActionListener(anzeige2);		tiefKatrina.addActionListener(anzeige2);		mittelhochSven.addActionListener(anzeige2);		tieftiefRolf.addActionListener(anzeige2);		extratiefCorinna.addActionListener(anzeige2);		hochSerimas.addActionListener(anzeige1);		tiefKatrina.addActionListener(anzeige1);		mittelhochSven.addActionListener(anzeige1);		tieftiefRolf.addActionListener(anzeige1);		extratiefCorinna.addActionListener(anzeige1);		hochSerimas.start();		tiefKatrina.start();		mittelhochSven.start();		tieftiefRolf.start();		extratiefCorinna.start();		while (true) {		}	}}