package pr2.a05.erste;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;import lernhilfe2013ss.event.weather.WeatherSymbol;import lernhilfe2013ss.graphics.DirtyPainter;import pr2.a05.Wetterzustand2;public class Wetteranzeige implements ActionListener {	protected WeatherSymbol weatherMan;	protected DirtyPainter weatherPaint;	public Wetteranzeige() {		weatherMan = new WeatherSymbol(0, 0);		weatherPaint = new DirtyPainter();		weatherPaint.add(weatherMan);		weatherPaint.showDrawing();	}	public void actionPerformed(ActionEvent e) {		String cmd = e.getActionCommand();		switch (cmd) {		case Wetterzustand2.SONNIG:			weatherMan.changeToSunny();			break;		case Wetterzustand2.WOLKIG:			weatherMan.changeToCloudy();			break;		case Wetterzustand2.BEDECKT:			weatherMan.changeToOvercast();			break;		case Wetterzustand2.REGEN:			weatherMan.changeToRain();			break;		case Wetterzustand2.GEWITTER:			weatherMan.changeToTempest();			break;		}		weatherPaint.repaint();	}}