package pr2.a05.zweite;import lernhilfe2013ss.event.Timer;public class WetterSimulation {	public static void main(String[] args) {		WetterModel m = new WetterModel();		Timer hochSerimas = new Timer(200);		Timer tiefKatrina = new Timer(300);		Timer mittelhochSven = new Timer(400);		Timer tieftiefRolf = new Timer(600);		Timer extratiefCorinna = new Timer(800);		WetterDienst zc = new WetterDienst(m);		Wetteranzeige v = new Wetteranzeige();		m.addPropertyChangeListener(v);		hochSerimas.addActionListener(zc);		tiefKatrina.addActionListener(zc);		mittelhochSven.addActionListener(zc);		tieftiefRolf.addActionListener(zc);		extratiefCorinna.addActionListener(zc);		hochSerimas.setActionCommand(Wetterzustand.BEDECKT.toString());		tiefKatrina.setActionCommand(Wetterzustand.SONNIG.toString());		mittelhochSven.setActionCommand(Wetterzustand.WOLKIG.toString());		tieftiefRolf.setActionCommand(Wetterzustand.REGEN.toString());		extratiefCorinna.setActionCommand(Wetterzustand.GEWITTER.toString());		hochSerimas.start();		tiefKatrina.start();		mittelhochSven.start();		tieftiefRolf.start();		extratiefCorinna.start();	}}