package pr2.a05.zweite;import java.beans.PropertyChangeEvent;import java.beans.PropertyChangeListener;import lernhilfe2013ss.event.weather.WeatherSymbol;import lernhilfe2013ss.graphics.DirtyPainter;public class Wetteranzeige implements PropertyChangeListener {	protected WeatherSymbol weatherMan;	protected DirtyPainter weatherPaint;	public Wetteranzeige() {		weatherMan = new WeatherSymbol(0, 0);		weatherPaint = new DirtyPainter();		weatherPaint.add(weatherMan);		weatherPaint.showDrawing();	}	public void propertyChange(PropertyChangeEvent evt) {		Wetterzustand zustand = (Wetterzustand) evt.getNewValue();		switch (zustand) {		case SONNIG:			weatherMan.changeToSunny();			break;		case WOLKIG:			weatherMan.changeToCloudy();			break;		case BEDECKT:			weatherMan.changeToOvercast();			break;		case REGEN:			weatherMan.changeToRain();			break;		case GEWITTER:			weatherMan.changeToTempest();			break;		}		weatherPaint.repaint();	}}