package pr2.a07;import java.beans.PropertyChangeEvent;import java.beans.PropertyChangeListener;import java.beans.PropertyChangeSupport;public class SmileyModel {	private final PropertyChangeSupport changer;	private int eyeAngle;	private boolean isSmiling;	public SmileyModel() {		changer = new PropertyChangeSupport(this);	}		public void addPropertyChangeListener(PropertyChangeListener I) {		changer.addPropertyChangeListener(I);	}		public void removePropertyChangeListener(PropertyChangeListener I) {		changer.removePropertyChangeListener(I);	}		public void firePropertyChange(PropertyChangeEvent e) {		changer.firePropertyChange(e);	}		public void setEyeAngle(int eyeAngle) {		int oldEyeAngle = this.eyeAngle;		this.eyeAngle = eyeAngle;		changer.firePropertyChange("MODEL_UPDATE", oldEyeAngle, eyeAngle);	}		public void setSmile(boolean isSmiling) {		boolean oldIsSmiling = this.isSmiling;		this.isSmiling = isSmiling;		changer.firePropertyChange("MODEL_UPDATE", oldIsSmiling, isSmiling);	}		public int getEyeAngle() {		return eyeAngle;	}		public boolean getIsSmiling() {		return isSmiling;	}}