package pr2.a07;import java.awt.Container;import java.awt.Dimension;import java.awt.FlowLayout;import javax.swing.JFrame;public class SmileyTest {	public static void main(String[] args) {		JFrame frame = new JFrame("Hello Smiley");		Container container = frame.getContentPane();		container.setLayout(new FlowLayout());		SmileyModel m1 = new SmileyModel(0,0,30,40,90,true);		SmileyModel m2 = new SmileyModel(0,0,30,40,180,false);		SmileyModel m3 = new SmileyModel(0,0,30,40,0,true);		SmileyModel m4 = new SmileyModel(0,0,30,40,270,false);		SmileyView smiley1 = new SmileyView(m1);		SmileyView smiley2 = new SmileyView(m1);		SmileyView smiley3 = new SmileyView(m2);		SmileyView smiley4 = new SmileyView(m3);		SmileyView smiley5 = new SmileyView(m4);		m1.addPropertyChangeListener(smiley1);		m2.addPropertyChangeListener(smiley2);		m3.addPropertyChangeListener(smiley2);		m4.addPropertyChangeListener(smiley2);		smiley1.setPreferredSize(new Dimension(100,100));		smiley2.setPreferredSize(new Dimension(100,100));		smiley3.setPreferredSize(new Dimension(100,100));		smiley4.setPreferredSize(new Dimension(100,100));		smiley5.setPreferredSize(new Dimension(100,100));		container.add(smiley1);		container.add(smiley2);		container.add(smiley3);		container.add(smiley4);		container.add(smiley5);		frame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);		frame.setSize(550, 100);        frame.setVisible(true);	        m1.setY(0);        m1.setX(0);	}}