package pr2.a07;import java.awt.Container;import java.awt.Dimension;import java.awt.FlowLayout;import javax.swing.JFrame;public class SmileyTest {	public static void main(String[] args) {		JFrame frame = new JFrame("Hello Smiley");		Container container = frame.getContentPane();		container.setLayout(new FlowLayout());		SmileyView smiley1 = new SmileyView(new SmileyModel(), 0,0,300,400,50,true);		SmileyView smiley2 = new SmileyView(new SmileyModel(), 20,30,300,400,50,true);		smiley1.setPreferredSize(new Dimension(200,200));		smiley2.setPreferredSize(new Dimension(200,200));		container.add(smiley1);		container.add(smiley2);		frame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);		frame.setSize(600, 600);        frame.setVisible(true);			}}