package pr2.a07;import java.awt.BorderLayout;import java.awt.Container;import javax.swing.JFrame;public class SmileyTest {	public static void main(String[] args) {		JFrame frame = new JFrame("Hello Smiley");		Container container = frame.getContentPane();		container.setLayout(new BorderLayout());		container.add(new SmileyView(new SmileyModel(), 0,0,30,40,50,true),BorderLayout.EAST);		container.add(new SmileyView(new SmileyModel(), 0,0,50,50,50,true),BorderLayout.WEST);		frame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);		frame.setSize(500, 500);        frame.setVisible(true);			}}