package pr2.a07;import java.awt.Container;import java.awt.Dimension;import java.awt.FlowLayout;import javax.swing.JFrame;public class SmileyTest {	public static void main(String[] args) {		JFrame frame = new JFrame("Hello Smiley");		Container container = frame.getContentPane();		container.setLayout(new FlowLayout());		SmileyModel m1 = new SmileyModel(0,0,30,40,90,true);		SmileyModel m2 = new SmileyModel(0,0,30,40,90,false);		SmileyView smiley1 = new SmileyView(m1);		SmileyView smiley2 = new SmileyView(m2);		m1.addPropertyChangeListener(smiley1);		m2.addPropertyChangeListener(smiley2);		smiley1.setPreferredSize(new Dimension(200,200));		smiley2.setPreferredSize(new Dimension(200,200));		container.add(smiley1);		container.add(smiley2);		frame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);		frame.setSize(600, 600);        frame.setVisible(true);	        m1.setY(0);        m1.setX(0);	}}