package pr2.a08;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;import java.beans.PropertyChangeEvent;import java.beans.PropertyChangeListener;public class ACPEEventPrinter implements ActionListener, PropertyChangeListener{	public void actionPerformed(ActionEvent e) {		System.out.println(e.getActionCommand());	}	public void propertyChange(PropertyChangeEvent evt) {		System.out.println("Property "+evt.getPropertyName()+ "changed");	}}