package pr2.a08;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;import java.beans.PropertyChangeEvent;import java.beans.PropertyChangeListener;public class ACPEEventPrinter implements ActionListener, PropertyChangeListener{	public void actionPerformed(ActionEvent e) {		System.out.printf("%20s%20s%15s%10s%10s%10s",						"Fired property ActionEvent!",						"ActionCommand: ",						e.getActionCommand(),						"Timestamp: ",						e.getWhen());	}	public void propertyChange(PropertyChangeEvent evt) {		System.out.printf("%20s%20s%15s%10s%10s%10s",						"Fired property change event!",						"PropertyName: ",evt.getPropertyName(),						evt.getOldValue(),						"-->",						evt.getNewValue()+"\n");	}}