package pr2.a08;import java.awt.Point;public class Circle extends Point {	protected int r;	protected int d;	protected int rotation;	public Circle(int x, int y, int r, int rotation) {		super(x, y);		this.r = r;		this.d = 2*r;		this.rotation = rotation;	} 		public Circle(int x, int y, int r) {		super(x, y);		this.r = r;		this.d = 2*r;		this.rotation = 0;	}	public Circle(Circle c) {		this(c.x, c.y, c.r);	}	public Point getCenter() {		return new Point(x + r, y + r);	}	public int getRotation() {		return rotation;	}		public int getRadius() {		return r;	}	public int getDiameter() {		return d;	}		public void setRadius(int r) {		this.r = r;	}	public void setDiamater(int d) {		this.d = d;	}		public void addRotation(int angle) {		rotation+=angle;	}		public void moveRelative(int dx, int dy) {		x += dx;		y += dy;	}	public void setPosition(int x, int y) {		this.x = x;		this.y = y;	}}