package pr2.a08;import java.awt.Container;import java.awt.Dimension;import java.awt.FlowLayout;import javax.swing.JFrame;public class FirstGui {	public static void main(String[] args) {		JFrame frame = new JFrame("Hello Smiley");		Container container = frame.getContentPane();		container.setLayout(new FlowLayout());		SmileyModel m1 = new SmileyModel(0,0,30,40,90,true);		SmileyModel m2 = new SmileyModel(0,0,30,40,180,false);		SmileyModel m3 = new SmileyModel(0,0,30,40,0,true);		SmileyModel m4 = new SmileyModel(0,0,30,40,270,false);		ACPEEventPrinter ep = new ACPEEventPrinter();		FirstSmileyPanel smiley1 = new FirstSmileyPanel(m1);		FirstSmileyPanel smiley2 = new FirstSmileyPanel(m1);		FirstSmileyPanel smiley3 = new FirstSmileyPanel(m2);		FirstSmileyPanel smiley4 = new FirstSmileyPanel(m3);		FirstSmileyPanel smiley5 = new FirstSmileyPanel(m4);		m1.addPropertyChangeListener(smiley1);		m1.addPropertyChangeListener(ep);		m2.addPropertyChangeListener(smiley2);		m2.addPropertyChangeListener(ep);		m3.addPropertyChangeListener(smiley2);		m3.addPropertyChangeListener(ep);		m4.addPropertyChangeListener(smiley2);		m4.addPropertyChangeListener(ep);		smiley1.setPreferredSize(new Dimension(100,100));		smiley2.setPreferredSize(new Dimension(100,100));		smiley3.setPreferredSize(new Dimension(100,100));		smiley4.setPreferredSize(new Dimension(100,100));		smiley5.setPreferredSize(new Dimension(100,100));		container.add(smiley1);		container.add(smiley2);		container.add(smiley3);		container.add(smiley4);		container.add(smiley5);		frame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);		frame.setSize(550, 100);        frame.setVisible(true);	        m1.setX(m1.x+1);        m2.setY(m2.y+1);        m3.setEyeSize(m3.eyeSize-1);        m4.setSmile(true);	}}