package pr2.a09;import java.awt.BorderLayout;import java.awt.event.ActionListener;import java.beans.PropertyChangeEvent;import java.beans.PropertyChangeListener;import javax.swing.JLabel;import javax.swing.JPanel;import javax.swing.JTextField;public class AngleAdjustmentPanel extends JPanel implements PropertyChangeListener {	protected JTextField angleField;	protected CentralController c;		public AngleAdjustmentPanel(CentralController c) {		this.c=c;		setLayout(new BorderLayout());		angleField = new JTextField(10);		angleField.setActionCommand(Command.INPUT_EYEANGLE_WITH_JTEXTFIELD.toString());		angleField.addActionListener(c);		angleField.setText(Integer.toString(c.m.getEyeAngle()));		add(new JLabel("Eye Angle"),BorderLayout.NORTH);		add(angleField, BorderLayout.SOUTH);	}	public void propertyChange(PropertyChangeEvent evt) {		if (evt.getPropertyName().equals(Command.MODEL_UPDATE.toString())) { 			angleField.setText(Integer.toString(c.m.getEyeAngle()));		}			}}