package pr2.a09;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;import javax.swing.JButton;import javax.swing.JCheckBox;import javax.swing.JTextField;public class CentralController implements ActionListener {	protected SmileyModel m;		public CentralController (SmileyModel m) {		this.m = m;	}	public void actionPerformed(ActionEvent e) {		switch (Command.valueOf(e.getActionCommand())) {		case INPUT_HEADRADIUS_WITH_JTEXTFIELD: 			JTextField headRadiusField = (JTextField)e.getSource();			m.setRadius(Integer.parseInt(headRadiusField.getText()));			break;		case INPUT_EYEANGLE_WITH_JTEXTFIELD:			JTextField eyeAngleField = (JTextField)e.getSource();			m.setEyeAngle(Integer.parseInt(eyeAngleField.getText()));			break;		case INPUT_EYERADIUS_WITH_JTEXTFIELD:			JTextField eyeRadiusField = (JTextField)e.getSource();			m.setEyeRadius(Integer.parseInt(eyeRadiusField.getText()));			break;		case INPUT_SMILE_WITH_JCHECKBOX:			JCheckBox smileBox = (JCheckBox)e.getSource();			m.setSmile(smileBox.isSelected());			break;		case INPUT_SETPLEASED_WITH_JBUTTON:			m.setPleased();			break;		case INPUT_SETSAD_WITH_JBUTTON:			m.setSad();			break;		default:			break;		}	}}