package pr2.a09;import java.awt.FlowLayout;import java.beans.PropertyChangeEvent;import java.beans.PropertyChangeListener;import javax.swing.JButton;import javax.swing.JCheckBox;import javax.swing.JPanel;//Gearypublic class EmotionPanel extends JPanel implements PropertyChangeListener{	protected JCheckBox emoCheckbox;	protected JButton happyButton;	protected JButton sadButton;	protected CentralController c;		public EmotionPanel(CentralController c) {		this.c=c;		setLayout(new FlowLayout());		happyButton = new JButton("Make really Happy");		sadButton = new JButton("Make really Sad");		emoCheckbox = new JCheckBox("Happy?");		refreshCheckBox();		happyButton.setActionCommand(Command.INPUT_SETPLEASED_WITH_JBUTTON.toString());		sadButton.setActionCommand(Command.INPUT_SETSAD_WITH_JBUTTON.toString());		emoCheckbox.setActionCommand(Command.INPUT_SMILE_WITH_JCHECKBOX.toString());		happyButton.addActionListener(c);		sadButton.addActionListener(c);		emoCheckbox.addActionListener(c);		add(happyButton);		add(sadButton);		add(emoCheckbox);	}		public void refreshCheckBox() {		emoCheckbox.setSelected(c.m.isSmiling());		System.out.println(c.m.isSmiling());	}		public void propertyChange(PropertyChangeEvent evt) {		if (evt.getPropertyName().equals(Command.MODEL_UPDATE.toString())) {			refreshCheckBox();		}	}		}