package pr2.a09;import java.awt.BorderLayout;import java.awt.event.ActionListener;import java.beans.PropertyChangeEvent;import java.beans.PropertyChangeListener;import javax.swing.JLabel;import javax.swing.JPanel;import javax.swing.JTextField;public class EyeAdjustmentPanel extends JPanel implements PropertyChangeListener {	protected CentralController c;	protected JTextField eyeSizeField;		public EyeAdjustmentPanel(CentralController c) {		this.c=c;		setLayout(new BorderLayout());		eyeSizeField = new JTextField(10);		eyeSizeField.setText(Integer.toString(c.m.getEyeSize()));		eyeSizeField.setActionCommand(Command.INPUT_EYERADIUS_WITH_JTEXTFIELD.toString());		eyeSizeField.addActionListener(c);		add(new JLabel("Eye Size"),BorderLayout.NORTH);		add(eyeSizeField, BorderLayout.SOUTH);	}	public void propertyChange(PropertyChangeEvent evt) {		if (evt.getPropertyName().equals(Command.MODEL_UPDATE.toString())) { 			eyeSizeField.setText(Integer.toString(c.m.getEyeSize()));		}	}}