package pr2.a09;import java.awt.BorderLayout;import java.awt.event.ActionListener;import java.beans.PropertyChangeEvent;import java.beans.PropertyChangeListener;import javax.swing.JLabel;import javax.swing.JPanel;import javax.swing.JTextField;public class HeadAjustmentPanel extends JPanel implements PropertyChangeListener{	protected CentralController c;	protected JTextField headSizeField;		public HeadAjustmentPanel(CentralController c) {		this.c=c;		setLayout(new BorderLayout());		headSizeField = new JTextField(10);		headSizeField.setText(Integer.toString(c.m.getRadius()));		headSizeField.setActionCommand(Command.INPUT_HEADRADIUS_WITH_JTEXTFIELD.toString());		headSizeField.addActionListener(c);		add(new JLabel("Head Radius"),BorderLayout.NORTH);		add(headSizeField, BorderLayout.SOUTH);	}	public void propertyChange(PropertyChangeEvent evt) {		if (evt.getPropertyName().equals(Command.MODEL_UPDATE.toString())) { 			headSizeField.setText(Integer.toString(c.m.getRadius()));		}	}}