package pr2.a09;import java.awt.BorderLayout;import java.awt.Color;import java.awt.Container;import java.awt.FlowLayout;import javax.swing.JFrame;import javax.swing.JPanel;public class Smiley {	public static void main(String[] args) {		final int YOFFSET = 60;		JFrame frame = new JFrame("Hello Smiley");		Container container = frame.getContentPane();		container.setLayout(new BorderLayout());			SmileyModel m1 = new SmileyModel(250,250-YOFFSET,30,30,30,true);		CentralController c = new CentralController(m1);				ACPEEventPrinter ep = new ACPEEventPrinter();		FirstSmileyPanel smiley1 = new FirstSmileyPanel(m1);				m1.addPropertyChangeListener(smiley1);		m1.addPropertyChangeListener(ep);		smiley1.setBackground(Color.black);				JPanel ajustmentPanel = new JPanel();		ajustmentPanel.setLayout(new FlowLayout());		HeadAjustmentPanel headAdjust = new HeadAjustmentPanel(c);		AngleAdjustmentPanel angleAdjust = new AngleAdjustmentPanel(c);		EyeAdjustmentPanel eyeAdjust = new EyeAdjustmentPanel(c);		EmotionPanel emotionSet = new EmotionPanel(c);				m1.addPropertyChangeListener(headAdjust);		m1.addPropertyChangeListener(angleAdjust);		m1.addPropertyChangeListener(eyeAdjust);		m1.addPropertyChangeListener(emotionSet);				ajustmentPanel.add(headAdjust);		ajustmentPanel.add(angleAdjust);			ajustmentPanel.add(eyeAdjust);			ajustmentPanel.add(emotionSet);					container.add(smiley1, BorderLayout.CENTER);		container.add(ajustmentPanel, BorderLayout.NORTH);		container.add(new EmotionPanel(c), BorderLayout.SOUTH);		frame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);		frame.setSize(500, 500);        frame.setVisible(true);	           	}}